    private void jb22ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jb22ActionPerformed
        // TODO add your handling code here:
        String answer;
        secondName = Double.parseDouble(jtxt1.getText());
        if (operations == "+") {
            result = firstName + secondName;
            answer = String.format("%.4f", result);
            jtxt1.setText(answer);
        } else if (operations == "-") {
            result = firstName - secondName;
            answer = String.format("%.4f", result);
            jtxt1.setText(answer);
        } else if (operations == "*") {
            result = firstName * secondName;
            answer = String.format("%.4f", result);
            jtxt1.setText(answer);
        } else if (operations == "/") {
            result = firstName / secondName;
            answer = String.format("%.4f", result);
            jtxt1.setText(answer);
        }
    }//GEN-LAST:event_jb22ActionPerformed

