    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        // TODO add your handling code here:
        double pepsi = 30;
        double up7 = 40;
        double cocokala = 35;
        double juice = 50;
        double tea = 20;
        double coffee = 70;
        if (jComboBox1.getSelectedItem().equals("pepsi")) {
            String pep = String.format("%.2f", pepsi);
            costOfDrink.setText(pep);
        } else if (jComboBox1.getSelectedItem().equals("7up")) {
            String up = String.format("%.2f", up7);
            costOfDrink.setText(up);
        } else if (jComboBox1.getSelectedItem().equals("Cocokala")) {
            String coco = String.format("%.2f", cocokala);
            costOfDrink.setText(coco);
        } else if (jComboBox1.getSelectedItem().equals("Juice")) {
            String juic = String.format("%.2f", juice);
            costOfDrink.setText(juic);
        } else if (jComboBox1.getSelectedItem().equals("Tea")) {
            String te = String.format("%.2f", tea);
            costOfDrink.setText(te);
        } else if (jComboBox1.getSelectedItem().equals("Coffee")) {
            String coffe = String.format("%.2f", coffee);
            costOfDrink.setText(coffe);
        } else if (jComboBox1.getSelectedItem().equals("Select a drink")) {
            costOfDrink.setText(null);
        }

    }//GEN-LAST:event_jComboBox1ActionPerformed

